package Programming;

public class Even_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 100; i++) {
			if (i % 2 == 0) // For odd number make changes i.e ==0 for even and ==1 for odd number.
			{
				System.out.println(i);
			}
		}

	}

}
