package Programming;

public class Greatest_Among_Two_Nums {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a = 20;
       int b = 10;
       if (a > b)
       {
    	   System.out.println("a is greater than b");
       }
       else
       {
    	  System.out.println("b is greater than a"); 
       }
	}

}
