package Programming;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a , b; // Declaring the variable
		a = 5;     // Assigning the values to the variable
		b = 5;
		int c = a+b; //Arithmetic Operation
		System.out.println("Addition of Two Numbers are: "+c);
		
		c = a - b;
		System.out.println("Subtraction of Two Numbers are: "+c);
		
		c = a * b;
		System.out.println("Multiplication of Two Numbers are: "+c);
		
		c = a / b;
		System.out.println("Division of Two Numbers are: "+c);
		
		c = a * b;
		System.out.println("Modulas of Two Numbers are: "+c);
	}
	

}
